$(document).ready(function(){deviceWidth=$(window).width();let navExpand=0;const a=$("header nav .nav-main");const b=$("#nav-expand");if(deviceWidth<1200){b.click(function(){if(navExpand==0){$("header").addClass("active");a.addClass("active");b.addClass("active");navExpand=1}else{$("header").removeClass("active");a.removeClass("active");b.removeClass("active");navExpand=0}})}if(deviceWidth>1200){$(".nav-main li.li-search a").on("click",function(c){c.preventDefault();$(".search-input").addClass("show")});$(".search-input #closeSearch").on("click",function(){$(".search-input").removeClass("show")})}});